import React, { useState } from 'react'

function Signin({connexion}) {
    const [id, setId] = useState("")
    const [mdp, setMdp] = useState("")
    const[confirm, setConfirm] = useState("")

    function register() {
        return mdp === confirm ? connexion() : alert("Les mots de passe ne correspondent pas");
    }

    function submitForm(e) {
        e.preventDefault();

    }

    return (
        <div className="signin">
            <h1>Register Account</h1>
            <form method="POST" action="" onSubmit={submitForm}>
                {/* <label for="username">Username</label> */}
                <input type="text" name="username" id="username" placeholder="Usename" onChange={setId}/>
                <br/>
                {/* <label for="password">Mot de passe</label> */}
                <input type="password" name="password" id="password" placeholder="Password" onChange={setMdp}/>
                <br/>
                {/* <label for="confirm">Confirm mot de passe</label> */}
                <input type="password" name="confirm" id="confirm" placeholder="Confirm Password" onChange={setConfirm}/>
                <br/>
                <input type="submit" value="Register" onClick={register}/>
                <input type="reset" value="Annuler"/>
                <br/>
            </form>
        </div>);
}

export default Signin
