import React, { useState } from 'react'

function Signin({connexion}) {
    const [id, setId] = useState("")
    const [mdp, setMdp] = useState("")
    const[confirm, setConfirm] = useState("")



    function submitForm(e) {
        e.preventDefault();
    }

    function register() {
        setMdp(document.getElementById("password").value);
        setId(document.getElementById("username").value);
        setConfirm(document.getElementById("confirm").value);
        return mdp === confirm ? alert("{connexion}") : alert("Les mots de passe ne correspondent pas");
    }

    function annuler() {
        setId("")
        setMdp("")
        setConfirm("")
    }

    return (
        <div className="signin">
            <h1>Register Account</h1>
            <form method="POST" action="" onSubmit={submitForm}>
                {/* <label for="username">Username</label> */}
                <input type="text" name="username" id="username" placeholder="Usename"/>
                <br/>
                {/* <label for="password">Mot de passe</label> */}
                <input type="password" name="password" id="password" placeholder="Password"/>
                <br/>
                {/* <label for="confirm">Confirm mot de passe</label> */}
                <input type="password" name="confirm" id="confirm" placeholder="Confirm Password"/>
                <br/>
                <input type="submit" value="Register" onClick={connexion}/>
                <input type="reset" value="Annuler" onClick={annuler}/>
                <br/>
            </form>
        </div>);
}

export default Signin
